import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-card-box',
  templateUrl: './card-box.component.html',
  styleUrls: ['./card-box.component.scss'],
})
export class CardBoxComponent implements OnInit {
  @Input() cor = 'info';
  @Input() quantidade = 0;
  @Input() titulo = '';
  @Input() icon = 'fas fa-file-pdf';
  @Input() rota: string;
  constructor() {}

  ngOnInit(): void {}
}
<!-- <div class="col-lg-2 col-4"> -->
<!-- small card -->
<a [routerLink]="[ rota ]">
  <div class="small-box bg-{{ cor }}">
    <div class="inner">
      <h3 class="color-white">{{ quantidade }}</h3>

      <p class="color-white">{{ titulo }}</p>
    </div>
    <div class="icon">
      <i class="{{ icon }}"></i>
    </div>
    <a class="small-box-footer color-white">
      Visualizar <i class="fas fa-arrow-circle-right color-white"></i>
    </a>
  </div>
</a>
<!-- </div> -->
